-- run this for updating employees

create table TEMP_ADDRESS (
	EMPLOYEE_ID VARCHAR(4),
	STREET_NO VARCHAR(50),
	STREET_NAME VARCHAR(50),
	CITY VARCHAR(50),
	COUNTRY VARCHAR(50)
);


BEGIN 
FOR R IN(SELECT * FROM TEMP_ADDRESS) 
LOOP 
UPDATE EMPLOYEE
SET ADDRESS = R.STREET_NO||' '||R.STREET_NAME||', '||R.CITY||', '||R.COUNTRY 
WHERE EMPLOYEE_ID = R.EMPLOYEE_ID;
END LOOP;
END;
/

DROP TABLE TEMP_ADDRESS;
